package JavaLevel1;

public class breakAndContinue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// break and continue and also return
		for(int i=0;i<=10;i++) {
			System.out.println("Ayman Sajid"+i);
			if(i==5) {
				
				break;
				
			}
		}
		int j=1;
		while(j<10) {
			j++;
			
			if(j==7) {
				continue;
			}
			System.out.println("Samimur Rahman Sakib"+j);
			
			
		}
		

	}

}
